if ($_POST) {\n    $content = $_POST['content'];\n    $by = $_SESSION['exp_user']['username'];\n    $dt = date("F j, Y, g:i a");\n\n    $pdo = new PDO('mysql:host=your_host;dbname=your_db', 'your_username', 'your_password');\n    $stmt = $pdo->prepare('UPDATE tbl_intmsg SET time = :time, by = :by, content = :content');\n    $stmt->bindParam(':time', $dt);\n    $stmt->bindParam(':by', $by);\n    $stmt->bindParam(':content', $content);\n    $stmt->execute();\n}