ldap_set_option($ldap, LDAP_OPT_PROTOCOL_VERSION, 3);\nldap_set_option($ldap, LDAP_OPT_REFERRALS, 0);\n\n$filter = "(memberOf:1.2.840.113556.1.4.1941:=(CN=User1,DC=example,DC=com))";\n$result = ldap_search($ldap, $baseDN, $filter, array("distinguishedName"));\n\n$entries = ldap_get_entries($ldap, $result);\n\nforeach ($entries as $entry) {\n    echo $entry["distinguishedName"][0] . "\n";\n}