class Engine\n{\n    private $loader;\n    private $logger;\n\n    public function __construct(ResourceLoader $loader = null, Logger $logger = null)\n    {\n        $this->loader = $loader ?: self::createDefaultLoader();\n        $this->logger = $logger ?: self::createDefaultLogger();\n    }\n\n    private static function createDefaultLoader(): ResourceLoader\n    {\n        return new DefaultResourceLoader();\n    }\n\n    private static function createDefaultLogger(): Logger\n    {\n        return new DefaultLogger();\n    }\n}