function generateActivationKey($email) {\n    // Generate a random string using a CSPRNG\n    $randomString = bin2hex(random_bytes(16));\n\n    // Use a sufficient work factor (e.g., iteration count) when generating the hash\n    $hashedKey = hash_pbkdf2('sha256', $randomString, $email, 10000, 32, true);\n\n    // Base64-encode the hashed key to make it URL-safe\n    $activationKey = base64_encode($hashedKey);\n\n    return $activationKey;\n}