$closingDate = mktime(23, 59, 59, 3, 27, 2009);\n\nfunction f1($closingDate)\n{\n    return time() > $closingDate;\n}\n\nfunction f2($closingDate)\n{\n    return time() < $closingDate;\n}\n\nprintf('    Time: %u\nClosing: %u\n\nt > c: %u\nf1   : %u\n\nt < c: %u\nf2   : %u', \n    time(), \n    $closingDate, \n    time() > $closingDate,\n    f1($closingDate), \n    time() < $closingDate,\n    f2($closingDate));