$dsn = 'QuickBooksDSN'; // replace with your DSN name\n$username = ''; // leave blank, as the DSN has the credentials\n$password = ''; // leave blank, as the DSN has the credentials\n\ntry {\n    $conn = odbc_connect($dsn, $username, $password);\n    if (!$conn) {\n        throw new Exception(odbc_errormsg());\n    }\n    $sql = "INSERT INTO your_table (column1, column2) VALUES ('value1', 'value2')";\n    $exec = odbc_exec($conn, $sql);\n    if (!$exec) {\n        throw new Exception(odbc_errormsg());\n    }\n    echo "Success!";\n} catch (Exception $e) {\n    echo $e->getMessage();\n} finally {\n    odbc_close($conn);\n}