$output = 'Blah blah blah [[studentCount]]';\n\n// Define a callback function to replace the patterns\nfunction replacePattern($match) {\n    global $school;\n    $field = $match[1]; // The field name is the first capturing group\n    return $school->get($field);\n}\n\n// Use preg_replace_callback to replace the patterns\n$output = preg_replace_callback('/\[\[(.*?)\]\]/', 'replacePattern', $output);\n\necho $output;