<?php\nrequire_once("../includes/class.database.php");\nrequire_once("../includes/dbConnectInfo.inc");\nrequire_once("../includes/functions.php");\n\ninclude("../includes/conn.php");\n\n$category = filter_input(INPUT_GET, 'category', FILTER_SANITIZE_STRING);\n\n$mysqli = new mysqli(DB_HOST, DB_USER, DB_PASSWORD, DB_NAME);\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$stmt = $mysqli->prepare("SELECT * FROM media WHERE related_page_id = 4 AND type = 'copy' ORDER BY id ASC LIMIT 6");\n$stmt->execute();\n$result = $stmt->get_result();\n\n$media = "<?xml version=\"1.0\" encoding=\"UTF-8\" ?>\n";\n$media .= "<content>\n";\n\nwhile ($row = $result->fetch_assoc()) {\n    $media .= "<member>\n";\n    $body = htmlspecialchars($row['copy']);\n\n    if (empty($row['title'])) {\n        $media .= "<title><![CDATA[Team]]></title>\n";\n    } elseif (empty($row['path'])) {\n        $name = explode("/", $row['title'], 2);\n\n        $media .= "<name><![CDATA[" . htmlspecialchars($name[0]) . "]]></name>\n";\n        $media .= "<job><![CDATA[" . htmlspecialchars($name[1]) . "]]></job>\n";\n    }\n\n    if (!empty($body)) {\n        $media .= "<bio><![CDATA[" . $body . "]]></bio>\n";\n    }\n\n    $something = $row['id'];\n    $stmt1 = $mysqli->prepare("SELECT * FROM media WHERE assets = ?");\n    $stmt1->bind_param("i", $something);\n    $stmt1->execute();\n    $result1 = $stmt1->get_result();\n\n    $media .= "<images>";\n    while ($row1 = $result1->fetch_assoc()) {\n        $img = explode("/", $row1['path'], 2);\n        $media .= "<image url='" . htmlspecialchars($img[1]) . "' />";\n    }\n    $media .= "</images>\n";\n    $media .= "</member>\n";\n}\n\n$media .= "</content>";\necho $media;\n?>