// settings.json (initial file)\n{\n    "setting1": "value1",\n    "setting2": "value2"\n}\n\n// Settings class\nclass Settings {\n    private $file;\n    private $settings;\n\n    public function __construct($file) {\n        $this->file = $file;\n        $this->loadSettings();\n    }\n\n    private function loadSettings() {\n        $this->settings = json_decode(file_get_contents($this->file), true);\n    }\n\n    public function getSetting($key) {\n        return $this->settings[$key];\n    }\n\n    public function setSetting($key, $value) {\n        $this->settings[$key] = $value;\n        file_put_contents($this->file, json_encode($this->settings));\n    }\n}\n\n// Usage\n$settings = new Settings('settings.json');\necho $settings->getSetting('setting1');  // Outputs: value1\n$settings->setSetting('setting3', 'value3');