function Get($tag)\n{\n    $tagDir = $this->FS . $this->Tag($tag) . '/';\n    $files = array();\n    if (is_dir($tagDir)) {\n        $iterator = new RecursiveIteratorIterator(new RecursiveDirectoryIterator($tagDir));\n        foreach ($iterator as $file) {\n            if ($file->isFile()) {\n                $files[] = $file->getPathname();\n            }\n        }\n    }\n    return $files;\n}