function largestCommonSubstring($str1, $str2) {\n    $m = strlen($str1);\n    $n = strlen($str2);\n    $dp = array_fill(0, $m + 1, array_fill(0, $n + 1, 0));\n    $maxLen = 0;\n    $endPos = 0;\n\n    for ($i = 1; $i <= $m; $i++) {\n        for ($j = 1; $j <= $n; $j++) {\n            if ($str1[$i - 1] === $str2[$j - 1]) {\n                $dp[$i][$j] = $dp[$i - 1][$j - 1] + 1;\n                if ($dp[$i][$j] > $maxLen) {\n                    $maxLen = $dp[$i][$j];\n                    $endPos = $i;\n                }\n            }\n        }\n    }\n\n    return substr($str1, $endPos - $maxLen, $maxLen);\n}\n\n// Example usage:\n$str1 = "abcdef";\n$str2 = "zbcdfg";\necho largestCommonSubstring($str1, $str2); // Output: "bcd"