$mail = '...'; // your mail text here\n\n$sections = preg_split('/(Victim:|Involved parties:|Destroyed:|Dropped:)/', $mail, -1, PREG_SPLIT_DELIM_CAPTURE);\n\n$victim = array();\n$involvedParties = array();\n$destroyedItems = array();\n$droppedItems = array();\n\nforeach ($sections as $section) {\n    if (preg_match('/Victim: (.*)/', $section, $match)) {\n        $victim['name'] = trim($match[1]);\n        // extract other victim data...\n    } elseif (preg_match('/Involved parties:/', $section)) {\n        preg_match_all('/Name: (.*)\nSecurity: (.*)\nCorp: (.*)\nAlliance: (.*)\nFaction: (.*)\nShip: (.*)\nWeapon: (.*)\nDamage Done: (.*)/', $section, $matches, PREG_SET_ORDER);\n        foreach ($matches as $match) {\n            $party = array(\n                'name' => trim($match[1]),\n                'security' => trim($match[2]),\n                // extract other party data...\n            );\n            $involvedParties[] = $party;\n        }\n    } elseif (preg_match('/Destroyed:/', $section)) {\n        preg_match_all('/(.*)\nQty: (\d+)/', $section, $matches, PREG_SET_ORDER);\n        foreach ($matches as $match) {\n            $item = array(\n                'name' => trim($match[1]),\n                'quantity' => (int)$match[2],\n                // extract other item data...\n            );\n            $destroyedItems[] = $item;\n        }\n    } elseif (preg_match('/Dropped:/', $section)) {\n        preg_match_all('/(.*)\nQty: (\d+)/', $section, $matches, PREG_SET_ORDER);\n        foreach ($matches as $match) {\n            $item = array(\n                'name' => trim($match[1]),\n                'quantity' => (int)$match[2],\n                // extract other item data...\n            );\n            $droppedItems[] = $item;\n        }\n    }\n}\n\n// print_r($victim);\n// print_r($involvedParties);\n// print_r($destroyedItems);\n// print_r($droppedItems);