$mysqli = new mysqli('your_host', 'your_username', 'your_password', 'your_database');\n\n$sql = "SELECT \n          v.venue_name AS 'Venue', \n          COUNT(vol.id) AS 'Number Of Volunteers'\n        FROM \n          venues v\n          LEFT JOIN volunteers_2009 vol ON v.id = vol.venue_id\n        GROUP BY \n          v.venue_name\n        ORDER BY \n          v.venue_name ASC";\n\n$stmt = $mysqli->prepare($sql);\n$stmt->execute();\n$result = $stmt->get_result();\n\nwhile ($row = $result->fetch_assoc()) {\n    echo '<p>' . $row['Venue'] . ': ' . $row['Number Of Volunteers'] . '</p>';\n}