// Assuming you have a logout function\nfunction logout() {\n  // Get the current session ID\n  $session_id = session_id();\n\n  // Remove the online user from the database\n  $db = new PDO('mysql:host=your_host;dbname=your_db', 'your_username', 'your_password');\n  $stmt = $db->prepare("DELETE FROM online_users WHERE session_id = :session_id");\n  $stmt->bindParam(':session_id', $session_id);\n  $stmt->execute();\n\n  // Destroy the session\n  session_destroy();\n}