global $cat;\nglobal $post;\n$categories = get_categories('child_of=3');\nforeach ($categories as $cat) :\n    $postslist = get_posts(array('cat' => $cat->cat_ID, 'numberposts' => 1, 'order' => 'DESC'));\n    $post = $postslist[0]; // access the first post directly\n    $option = '<li id="'.get_post_meta($post->ID, 'id', true).'">';\n    $option .='<a class="preview" rel="'.get_post_meta($post->ID, 'thumbnail', true).'" ';\n    $option .='href="'.get_bloginfo('url').'/'.$post->post_name.'">';\n    $option .=$cat->cat_name;\n    $option .='</a>';\n    $option .='</li>';\n    echo $option;\nendforeach;