function convertTextToHtml($text) {\n    // Convert bold and italic text\n    $text = preg_replace('/\[(b|i)\](.*?)\[\/\1\]/', '<$1>$2</$1>', $text);\n\n    // Convert unordered lists\n    $text = preg_replace('/^- (.*)$/m', '<li>$1</li>', $text);\n    $text = preg_replace('/(<li>.*<\/li>\s?)+/', '<ul>$0</ul>', $text);\n\n    // Convert ordered lists\n    $text = preg_replace('/^# (.*)$/m', '<li>$1</li>', $text);\n    $text = preg_replace('/(<li>.*<\/li>\s?)+/', '<ol>$0</ol>', $text);\n\n    return $text;\n}\n\n// Example usage\n$text = '[b]Some bold text[/b]\n[i]Italic[/i]\n- List item 1\n- List item 2\n- List item 3\n# List item 1\n# List item 2\n# List item 3';\n\n$html = convertTextToHtml($text);\nprint $html;