function combineVerifiedAndUnverified($stores) {\n    $masterVerified = [];\n    $masterUnverified = [];\n\n    // Get all unique dates across all stores\n    $dates = [];\n    foreach ($stores as $store) {\n        foreach ($store['verified'] as $entry) {\n            $dates[] = $entry['date'];\n        }\n        foreach ($store['unverified'] as $entry) {\n            $dates[] = $entry['date'];\n        }\n    }\n    $dates = array_unique($dates);\n    sort($dates);\n\n    // Iterate over each date\n    foreach ($dates as $date) {\n        $verifiedEntries = [];\n        $unverifiedEntries = [];\n\n        // Check each store for verified and unverified entries on this date\n        foreach ($stores as $store) {\n            $verifiedEntry = null;\n            $unverifiedEntry = null;\n\n            foreach ($store['verified'] as $entry) {\n                if ($entry['date'] === $date) {\n                    $verifiedEntry = $entry;\n                    break;\n                }\n            }\n\n            foreach ($store['unverified'] as $entry) {\n                if ($entry['date'] === $date) {\n                    $unverifiedEntry = $entry;\n                    break;\n                }\n            }\n\n            // If any store has an unverified entry on this date, mark all entries as unverified\n            if ($unverifiedEntry) {\n                $unverifiedEntries[] = $unverifiedEntry;\n            } elseif ($verifiedEntry) {\n                $verifiedEntries[] = $verifiedEntry;\n            }\n        }\n\n        // Add entries to master arrays\n        if (!empty($verifiedEntries)) {\n            $masterVerified[] = [\n                'date' => $date,\n                'sales' => array_sum(array_column($verifiedEntries, 'sales')),\n                'cost' => array_sum(array_column($verifiedEntries, 'cost')),\n            ];\n        } elseif (!empty($unverifiedEntries)) {\n            $masterUnverified[] = [\n                'date' => $date,\n                'sales' => array_sum(array_column($unverifiedEntries, 'sales')),\n                'cost' => array_sum(array_column($unverifiedEntries, 'cost')),\n            ];\n        }\n    }\n\n    return [$masterVerified, $masterUnverified];\n}