$fd = inotify_init();\n$watch_descriptor = inotify_add_watch($fd, 'input.txt', IN_MODIFY);\n\nwhile (true) {\n    $events = inotify_read($fd);\n    foreach ($events as $event) {\n        if ($event['mask'] & IN_MODIFY) {\n            $fp = fopen("input.txt", 'r');\n            while ($line = fgets($fp)) {\n                echo $line;\n            }\n            fclose($fp);\n        }\n    }\n}