$sql = "SELECT SUBSTR(a.`title`, 1,1) FROM articles a WHERE a.`tag` = 'human_resources'";\n$results = db_query($sql);\n$alphabetString = ''; // Initialize an empty string\n\nwhile ($fields = db_fetch_array($results)) {\n    foreach($fields as $key => $value) {\n        $alphabetString .= $value; // Concatenate the values to the string\n    }\n}\n\n// Now you can use the $alphabetString in your second part of code\nif(stripos($alphabetString, "A") !== false) {\n    echo ('<a href="http://www.mysite.com/articles/A">A</a>');\n} else {\n    echo '<span class="inactive">A</span>';\n}\n\n// ... and so on for the rest of the alphabet