<?php\n$data = array(\n    array('name' => 'Item 1', 'price' => 10.99),\n    array('name' => 'Item 2', 'price' => 9.99),\n    array('name' => 'Item 3', 'price' => 12.99),\n    // ...\n);\n\n// Handle sorting\nif (isset($_GET['sort'])) {\n    switch ($_GET['sort']) {\n        case 'name':\n            usort($data, function($a, $b) {\n                return strcmp($a['name'], $b['name']);\n            });\n            break;\n        case 'price':\n            usort($data, function($a, $b) {\n                return $a['price'] - $b['price'];\n            });\n            break;\n    }\n}\n\n// Generate table\necho '<table id="sortable-table">';\necho '<thead>';\necho '<tr>';\necho '<th><a href="?sort=name">Name</a></th>';\necho '<th><a href="?sort=price">Price</a></th>';\necho '</tr>';\necho '</thead>';\necho '<tbody>';\nforeach ($data as $row) {\n    echo '<tr>';\n    echo '<td>' . $row['name'] . '</td>';\n    echo '<td>' . $row['price'] . '</td>';\n    echo '</tr>';\n}\necho '</tbody>';\necho '</table>';