session_start();\n\nif (!isset($_SESSION['selected_items'])) {\n    $_SESSION['selected_items'] = array();\n}\n\n// Add selected item to session array\nif (isset($_POST['select_item'])) {\n    $item_id = $_POST['select_item'];\n    $_SESSION['selected_items'][] = $item_id;\n}\n\n// Remove deselected item from session array\nif (isset($_POST['deselect_item'])) {\n    $item_id = $_POST['deselect_item'];\n    $index = array_search($item_id, $_SESSION['selected_items']);\n    if ($index !== false) {\n        unset($_SESSION['selected_items'][$index]);\n    }\n}