// assuming $rows is an array of rows from your MySQL query\necho "<form method=\"post\">";\necho "<table>";\n\nforeach ($rows as $row) {\n    echo "<tr>";\n    echo "<td>" . $row['id'] . "</td>";\n    echo "<td><input name=\"checkbox[]\" type=\"checkbox\" id=\"checkbox_" . $row['id'] . "\"";\n    if ($row['your_column_name'] == true) {\n        echo " checked";\n    }\n    echo "></td>";\n    echo "</tr>";\n}\n\necho "</table>";\necho "<input name=\"update\" type=\"submit\" id=\"update\" value=\"Update\">";\necho "</form>";