/**\n * Implements hook_url_alter().\n *\n * Alter the node URL to include a partial title for better SEO.\n */\nfunction mymodule_url_alter(&$path, &$options, $original_path) {\n  // Check if the node is of type 'article'\n  $node = node_load($options['nid']);\n  if ($node->type == 'article') {\n    // Append the partial title to the path\n    $title = drupal_substr($node->title, 0, 30); // trim title to 30 characters\n    $title = drupal_urlencode($title); // encode special characters\n    $path .= '/' . $title;\n  }\n}