if (isset($_GET["page"])) {\n    $page = $_GET["page"];\n\n    // Validate the page number\n    if (!is_numeric($page) || $page < 1) {\n        die("Invalid page number");\n    }\n\n    $con = new mysqli("localhost", "username", "password", "jmurano_pages");\n\n    if ($con->connect_error) {\n        die("Connection failed: " . $con->connect_error);\n    }\n\n    $stmt = $con->prepare("SELECT title, content FROM pageContent WHERE pageID = ?");\n    $stmt->bind_param("i", $page);\n    $stmt->execute();\n    $stmt->bind_result($title, $content);\n    $stmt->fetch();\n\n    echo "<title>$title</title>";\n    echo "</head>";\n    echo "<body>";\n    echo $content;\n    echo "</body></html>";\n\n    $stmt->close();\n    $con->close();\n}