// Search for includes in the PHP code\n$directory = './'; // Set the directory to search\n$fileTypes = array('.php', '.tpl'); // Set the file types to search\n\n$iterator = new RecursiveIteratorIterator(new RecursiveDirectoryIterator($directory));\nforeach ($iterator as $file) {\n    if (in_array(pathinfo($file, PATHINFO_EXTENSION), $fileTypes)) {\n        $content = file_get_contents($file);\n        if (preg_match('/include|require/', $content)) {\n            echo "Found include in file: " . $file . "\n";\n        }\n    }\n}