$stmt = $pdo->prepare('UPDATE A SET type = :type, sub_type = :sub_type, filename = :filename, filepath = :filepath, filesize = :filesize WHERE id = :id AND lock_status IS NULL');\n$stmt->bindParam(':type', $newType);\n$stmt->bindParam(':sub_type', $newSubType);\n$stmt->bindParam(':filename', $newFilename);\n$stmt->bindParam(':filepath', $newFilepath);\n$stmt->bindParam(':filesize', $newFilesize);\n$stmt->bindParam(':id', $id);\n$stmt->execute();