use Joomla\CMS\Factory;\nuse Joomla\CMS\Plugin\CMSPlugin;\nuse Joomla\CMS\User\UserHelper;\nuse PhpPasswordStrengthMeter\PasswordStrengthMeter;\n\nclass PlgUserPasswordStrength extends CMSPlugin\n{\n    public function onUserBeforeSave($user, $isnew)\n    {\n        $password = $user->password_clear;\n\n        $strengthMeter = new PasswordStrengthMeter();\n        $strength = $strengthMeter->check($password);\n\n        if ($strength < 50) {\n            // Password is too weak, throw an error\n            throw new \Exception('Password is too weak');\n        }\n    }\n}