// Assuming $value is your JSON object\n\n// Sort the rows based on the "sort" value\nusort($value->row, function($a, $b) {\n    return $a->sort - $b->sort;\n});\n\n// Now, iterate over the sorted rows\nforeach ($value->row as $therow) {\n    echo "<div class='row " . $therow->type . "'>";\n    \n    foreach ($therow->widgets as $thewidgets) {\n        echo "<div class='widget'>";\n        echo $thewidgets->values[0];\n        echo "</div>";\n    }\n    \n    echo "</div>";\n}