// Video upload handling\nif ($_FILES['video']['size'] > 0) {\n  $upload_dir = 'uploads/';\n  $filename = basename($_FILES['video']['name']);\n  $upload_file = $upload_dir . $filename;\n  move_uploaded_file($_FILES['video']['tmp_name'], $upload_file);\n  \n  // Convert video to FLV using FFmpeg\n  $ffmpeg = 'ffmpeg -i ' . $upload_file . ' -ar 22050 -ab 128k -ac 2 -acodec aac -ac 2 -s 320x240 -vcodec flv ' . $upload_dir . $filename . '.flv';\n  exec($ffmpeg);\n}\n\n// Video playback using JW Player\n$video_file = 'uploads/video.flv';\n$player = 'jwplayer/jwplayer.swf';\n$player_params = 'file=' . $video_file . '&autostart=true';\necho '<object width="320" height="240" data="' . $player . '" type="application/x-shockwave-flash">';\necho '<param name="movie" value="' . $player . '" />';\necho '<param name="flashvars" value="' . $player_params . '" />';\necho '</object>';