<?php\nsession_start();\nif (isset($_GET["cmd"]))\n  $cmd = $_GET["cmd"];\nelse\n  die("You should have a 'cmd' parameter in your URL");\n\n$pk = $_GET["pk"];\n\n$con = mysqli_connect("localhost","root","geheim","ebay");\n\nif(!$con)\n{\n  die('Connection failed because of' .mysqli_connect_error());\n}\n\nif($cmd=="GetAuctionData")\n{\n  echo "<table border='1' width='100%'>\n  <tr>\n  <th>Username</th>\n  <th>Start Date</th>\n  <th>Description</th>\n  </tr>";\n\n  $sql="SELECT * FROM Auctions WHERE ARTICLE_NO =?";\n\n  $stmt = $con->prepare($sql);\n  $stmt->bind_param("s", $pk);\n  $stmt->execute();\n  $result = $stmt->get_result();\n\n  while ($row = $result->fetch_assoc())\n  {\n    echo "<tr>\n    <td>".$row['USERNAME']."</td>\n    <td>".$row['ARTICLE_NO']."</td>\n    <td>".$row['ARTICLE_NAME']."</td>\n    <td>".$row['SUBTITLE']."</td>\n    <td>".$row['CURRENT_BID']."</td>\n    <td>".$row['START_PRICE']."</td>\n    <td>".$row['BID_COUNT']."</td>\n    <td>".$row['QUANT_TOTAL']."</td>\n    <td>".$row['QUANT_SOLD']."</td>\n    <td>".$row['ACCESSSTARTS']."</td>\n    <td>".$row['ACCESSENDS']."</td>\n    <td>".$row['ACCESSORIGIN_END']."</td>\n    <td>".$row['USERNAME']."</td>\n    <td>".$row['BEST_BIDDER_ID']."</td>\n    <td>".$row['FINISHED']."</td>\n    <td>".$row['WATCH']."</td>\n    <td>".$row['BUYITNOW_PRICE']."</td>\n    <td>".$row['PIC_URL']."</td>\n    <td>".$row['PRIVATE_AUCTION']."</td>\n    <td>".$row['AUCTION_TYPE']."</td>\n    <td>".$row['ACCESSINSERT_DATE']."</td>\n    <td>".$row['ACCESSUPDATE_DATE']."</td>\n    <td>".$row['CAT_1_ID']."</td>\n    <td>".$row['CAT_2_ID']."</td>\n    <td>".$row['ARTICLE_DESC']."</td>\n    <td>".$row['COUNTRYCODE']."</td>\n    <td>".$row['LOCATION']."</td>\n    <td>".$row['CONDITIONS']."</td>\n    <td>".$row['REVISED']."</td>\n    <td>".$row['PAYPAL_ACCEPT']."</td>\n    <td>".$row['PRE_TERMINATED']."</td>\n    <td>".$row['SHIPPING_TO']."</td>\n    <td>".$row['FEE_INSERTION']."</td>\n    <td>".$row['FEE_FINAL']."</td>\n    <td>".$row['FEE_LISTING']."</td>\n    <td>".$row['PIC_XXL']."</td>\n    <td>".$row['PIC_DIASHOW']."</td>\n    <td>".$row['PIC_COUNT']."</td>\n    <td>".$row['ITEM_SITE_ID']."</td>\n    <td>".$row['STARTS']."</td>\n    <td>".$row['ENDS']."</td>\n    <td>".$row['ORIGIN_END']."</td>\n    </tr>\n    <tr><td></td></tr>";\n\n    if(!empty($row['PIC_URL'])) {\n      echo "<img src='".$row['PIC_URL']."' />";\n    } else {\n      echo "No image available";\n    }\n  }\n  echo "</table>";\n}\n\n$con->close();\n?>