$handle = fopen('infoboxes.csv', 'r');\nif (!$handle) {\n    throw new Exception('Failed to open CSV file');\n}\n\n$buffer = [];\n$batchSize = 1000; // adjust to your liking\n\nwhile (($data = fgetcsv($handle, 0, ',')) !== FALSE) {\n    $buffer[] = $data;\n    if (count($buffer) >= $batchSize) {\n        insertIntoDatabase($buffer);\n        $buffer = [];\n    }\n}\n\nif (!empty($buffer)) {\n    insertIntoDatabase($buffer);\n}\n\nfclose($handle);\n\nfunction insertIntoDatabase(array $data)\n{\n    $db = new PDO('mysql:host=localhost;dbname=dbpedia', 'username', 'password');\n    $stmt = $db->prepare('INSERT INTO infoboxes (title, ... other columns ...) VALUES (:title, ...)');\n    foreach ($data as $row) {\n        $stmt->execute($row);\n    }\n}