$sql="SELECT * FROM Auctions WHERE ARTICLE_NO ='$pk'";\n$sql2="SELECT ARTICLE_DESC FROM Auctions WHERE ARTICLE_NO ='$pk'";\n$htmlset = mysql_query($sql2);\n$row2 = mysql_fetch_array($htmlset);\n\n// Escape the HTML content to prevent XSS\n$htmlContent = htmlspecialchars($row2['ARTICLE_DESC']);\n\n// Create a JavaScript function to open the popup window\n$jsCode = <<<JS\nfunction openPopup() {\n  var childWindow = window.open("about:blank", "popup", "width=400,height=300");\n  childWindow.document.write("$htmlContent");\n  childWindow.document.close();\n}\nJS;\n\n// Echo the JavaScript code and the button to trigger the popup\necho "<script>$jsCode</script>";\necho "<button onclick='openPopup()'>Open Popup</button>";