$xml = new SimpleXMLElement('<chart/>');\n$xml->addChild('chart_data');\n$xml->addChild('chart_settings');\n\n// Add data to chart_data\n$data = $xml->chart_data->addChild('data');\n$data->addAttribute('label', 'Label 1');\n$data->addAttribute('value', '10');\n\n// Add settings to chart_settings\n$settings = $xml->chart_settings->addChild('settings');\n$settings->addAttribute('animation', 'true');\n\n// Generate XML string\n$xmlString = $xml->asXML();\n\n// Pass XML string to SWF file\necho '<object type="application/x-shockwave-flash" data="chart.swf?xml=' . urlencode($xmlString) . '"></object>';