<?php\n// data.php\n\n// Connect to database\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Get filters from $_GET\n$filters = array();\nif (isset($_GET['id'])) {\n    $filters[] = "id = '".mysqli_real_escape_string($conn, $_GET['id'])."'";\n}\nif (isset($_GET['name'])) {\n    $filters[] = "name LIKE '%".mysqli_real_escape_string($conn, $_GET['name'])."%'";\n}\nif (isset($_GET['type'])) {\n    $filters[] = "type = '".mysqli_real_escape_string($conn, $_GET['type'])."'";\n}\n\n// Apply filters to query\n$query = "SELECT * FROM table_name";\nif (!empty($filters)) {\n    $query .= " WHERE ".implode(" AND ", $filters);\n}\n\n// Execute query\n$result = $conn->query($query);\n\n// Output data in JSON format\n$data = array();\nwhile ($row = $result->fetch_assoc()) {\n    $data[] = $row;\n}\necho json_encode($data);\n\n$conn->close();\n?>