function log_session_activity($message) {\n    $log_file = 'session_activity.log';\n    $timestamp = date('Y-m-d H:i:s');\n    $session_id = session_id();\n    $user_id = $_SESSION['user_id'];\n    $log_message = "$timestamp - $session_id - $user_id - $message\n";\n    file_put_contents($log_file, $log_message, FILE_APPEND);\n}\n\n// Log session creation\nlog_session_activity('Session created');\n\n// Log session access\nlog_session_activity('Session accessed');\n\n// Log session destruction\nlog_session_activity('Session destroyed');