function isProfane($word, $dictionary) {\n    $minDistance = 2; // adjust this value to suit your needs\n    foreach ($dictionary as $profaneWord) {\n        $distance = levenshtein($word, $profaneWord);\n        if ($distance <= $minDistance) {\n            return true;\n        }\n    }\n    return false;\n}\n\n$dictionary = array('badword1', 'badword2', ...); // load your word list\n$word = 'a$$'; // input word to check\nif (isProfane($word, $dictionary)) {\n    echo 'Profane word detected!';\n} else {\n    echo 'Clean word!';\n}