$main_folder = '/path/to/main_folder'; // Set the main folder path\n$default_image = '/path/to/default_image.jpg'; // Set the default image path\n\n$folders = glob($main_folder . '/*', GLOB_ONLYDIR); // Get all subfolders\n\nforeach ($folders as $folder) {\n  $images = glob($folder . '/*.jpg'); // Get all .jpg images in the folder\n  $image = reset($images); // Get the first image\n\n  if ($image) {\n    // Validate the file type\n    $mime_type = mime_content_type($image);\n    if (strpos($mime_type, 'image/') === 0) {\n      echo '<img src="' . $image . '">'; // Display the image\n    } else {\n      echo '<img src="' . $default_image . '">'; // Display default image\n    }\n  } else {\n    echo '<img src="' . $default_image . '">'; // Display default image\n  }\n}