$ssrs_url = 'https://your-ssrs-server.com/ReportServer/ReportService.asmx';\n$report_path = '/YourReportFolder/YourReport';\n$username = 'your_username';\n$password = 'your_password';\n\n// Create a SOAP client\n$client = new SoapClient($ssrs_url . '?wsdl', array(\n    'login' => $username,\n    'password' => $password\n));\n\n// Render the report\n$report_data = $client->Render($report_path, 'PDF');\n\n// Display the report\nheader('Content-Type: application/pdf');\necho $report_data;