// Start output buffering to a temporary file\n$buffer_file = tempnam(sys_get_temp_dir(), 'output_buffer');\nob_start(function($buffer) use ($buffer_file) {\n    file_put_contents($buffer_file, $buffer);\n    return '';\n}, 1); // The '1' indicates that the buffer should be flushed after each output\n\n// Business Logic, etc\n\n// End output buffering and read from the temporary file\nob_end_clean();\necho apply_post_filter(file_get_contents($buffer_file));\nunlink($buffer_file);