$query = mysql_query("SELECT f.*, MAX(c.comment_date) AS last_comment_date\n                     FROM forums f\n                     LEFT JOIN comments c ON f.forum_id = c.comment_thread_id\n                     GROUP BY f.forum_id\n                     ORDER BY f.forum_order ASC");\n\nwhile ($row = mysql_fetch_assoc($query)) {\n    echo "<h3>Forum: {$row['forum_name']}</h3>";\n    echo "<div>Desc: {$row['forum_description']}</div>";\n    echo "<div>Last post: {$row['last_comment_date']}</div>";\n}