function catch_warning($errno, $errstr, $errfile, $errline) {\n    throw new Exception($errstr, $errno);\n}\n\nset_error_handler('catch_warning', E_WARNING);\n\ntry {\n    $records = dns_get_record($hostname, $type, $authns, $addtl);\n} catch (Exception $e) {\n    // Handle the warning as an exception\n    echo "Warning: " . $e->getMessage() . "\n";\n} finally {\n    restore_error_handler();\n}