require_once 'pChart.class.php';\n\n// Data\n$data = array(\n    array('Jan', 10),\n    array('Feb', 12),\n    array('Mar', 15)\n);\n\n// Create the chart\n$chart = new pChart(300, 200);\n$chart->setFontProperties('Fonts/tahoma.ttf', 8);\n$chart->setGraphArea(50, 30, 200, 150);\n$chart->drawScale(array('MinValue' => 0, 'MaxValue' => 20));\n$chart->drawBarChart($data);\n\n// Output the chart\nheader('Content-Type: image/png');\n$chart->render('chart.png');