$redis = new Redis();\n$redis->connect('localhost', 6379);\n\n$queryResult = $redis->get('query_result');\nif ($queryResult !== false) {\n    // Return cached result\n    return $queryResult;\n} else {\n    // Run the query and store the result in cache\n    $result = runQuery();\n    $redis->set('query_result', $result);\n    $redis->expire('query_result', 3600); // Cache for 1 hour\n    return $result;\n}