require_once 'PHPExcel.php';\n\n// Create a new PHPExcel object\n$objPHPExcel = new PHPExcel();\n\n// Add some data to the first sheet\n$objPHPExcel->getActiveSheet()->setCellValue('A1', 'Report 1');\n$objPHPExcel->getActiveSheet()->setCellValue('A2', 'This is the first report');\n\n// Create a new sheet for the second report\n$objPHPExcel->createSheet();\n$objPHPExcel->getActiveSheet()->setCellValue('A1', 'Report 2');\n$objPHPExcel->getActiveSheet()->setCellValue('A2', 'This is the second report');\n\n// Load the HTML reports into strings\n$report1 = file_get_contents('report1.html');\n$report2 = file_get_contents('report2.html');\n\n// Add the HTML reports to the Excel file\n$objPHPExcel->getActiveSheet()->setCellValue('A3', $report1);\n$objPHPExcel->getActiveSheet()->setCellValue('A4', $report2);\n\n// Save the Excel file\n$objWriter = new PHPExcel_Writer_Excel2007($objPHPExcel);\n$objWriter->save('reports.xlsx');