class DatabaseBackup {\n    private $dbType;\n    private $dbName;\n    private $username;\n    private $password;\n\n    public function __construct($dbType, $dbName, $username = null, $password = null) {\n        $this->dbType = $dbType;\n        $this->dbName = $dbName;\n        $this->username = $username;\n        $this->password = $password;\n    }\n\n    public function backup() {\n        switch ($this->dbType) {\n            case 'mysql':\n                $this->mysqlBackup();\n                break;\n            case 'postgres':\n                $this->postgresBackup();\n                break;\n            case 'sqlite':\n                $this->sqliteBackup();\n                break;\n            default:\n                throw new Exception('Unsupported database type');\n        }\n    }\n\n    private function mysqlBackup() {\n        exec("mysqldump -u {$this->username} -p{$this->password} {$this->dbName} > backup.sql");\n    }\n\n    private function postgresBackup() {\n        exec("pg_dump -U {$this->username} {$this->dbName} > backup.sql");\n    }\n\n    private function sqliteBackup() {\n        exec("sqlite3 {$this->dbName} '.backup backup.db'");\n    }\n}