$images = array(\n    array("boxes/blue.jpg", "blah.html"),\n    array("boxes/green.jpg", "blah.html"),\n    array("boxes/orange.jpg", "blah.html"),\n    array("boxes/pink.jpg", "blah.html"),\n    array("boxes/purple.jpg", "blah.html"),\n    array("boxes/red.jpg", "blah.html"),\n    array("boxes/yellow.jpg", "blah.html")\n);\n\n$boxes = array();\nfor ($i = 0; $i < 5; $i++) {\n    $rand = rand(0, count($images) - 1);\n    $boxes[] = $images[$rand];\n    unset($images[$rand]);\n    $images = array_values($images); // reindex the array\n}\n\nforeach ($boxes as $box) {\n    echo "<a href='" . $box[1] . "'><img src='" . $box[0] . "' /></a>";\n}