$ip = $_SERVER['REMOTE_ADDR'];\n$blocked_ips = array('10.0.0.0/8', '192.168.0.0/16');\n\nforeach ($blocked_ips as $blocked_ip) {\n    if (ip_in_range($ip, $blocked_ip)) {\n        die('Access denied');\n    }\n}\n\nfunction ip_in_range($ip, $range) {\n    $ip_parts = explode('.', $ip);\n    $range_parts = explode('.', $range);\n\n    for ($i = 0; $i < 4; $i++) {\n        if ((int)$ip_parts[$i] < (int)$range_parts[$i]) {\n            return false;\n        } elseif ((int)$ip_parts[$i] > (int)$range_parts[$i]) {\n            return true;\n        }\n    }\n    return true;\n}