// mymodule.module\nfunction mymodule_menu() {\n  $items['ajax/update-block'] = array(\n    'title' => 'Update block',\n    'page callback' => 'mymodule_update_block',\n    'access callback' => TRUE,\n    'ajax' => TRUE,\n  );\n  return $items;\n}\n\nfunction mymodule_update_block() {\n  // Update the block content here\n  $block_content = 'New block content';\n  return array(\n    '#type' => 'ajax',\n    '#commands' => array(\n      array(\n        'command' => 'replace',\n        'selector' => '#my-block',\n        'data' => $block_content,\n      ),\n    ),\n  );\n}