amespace YourNamespace;\n\nfunction wrap_wordpress_function($function) {\n    static $wrap = array(\n        'comment' => 'the_comment',\n        'comments' => 'have_comments',\n        'post' => 'the_post',\n        'posts' => 'have_posts'\n    );\n\n    if (!isset($wrap[$function])) {\n        throw new InvalidArgumentException("Invalid function name: $function");\n    }\n\n    $args = array_slice(func_get_args(), 1);\n    return call_user_func($wrap[$function], ...$args);\n}