$category_id = 1; // Replace with the ID of the category you want to display\n$args = array(\n    'posts_per_page' => -1, // Show all posts\n    'category' => $category_id,\n    'include_children' => true // Include child categories\n);\n\n$posts = get_posts($args);\n\nforeach ($posts as $post) {\n    // Display the post title and link\n    echo '<a href="' . get_permalink($post->ID) . '">' . get_the_title($post->ID) . '</a><br>';\n}